mapper.select:
SELECT LAWYER_ID, LAWYER_EMAIL, LAWYER_PASSWORD, LAWYER_NAME, LAWYER_STATE, LAWYER_PHONE, LAWYER_COMPANY, LAWYER_ADDRESS, LAWYER_QUALIFICATION, LAWYER_EDUCATION, LAWYER_IMAGE, CALL_PRICE, VIDEO_PRICE, VISIT_PRICE
FROM TBL_LAWYER L
WHERE LAWYER_ID = #{lawyerId}

SELECT COUNT(SOLUTION_CASE_ID)
FROM TBL_SOLUTION_CASE
WHERE LAWYER_ID = #{lawyerId}

SELECT COUNT(LEGAL_GUIDE_ID)
FROM TBL_LEGAL_GUIDE
WHERE LAWYER_ID = #{lawyerId}

SELECT COUNT(LAWYER_LIKE_ID)
FROM TBL_LAWYER_LIKE
WHERE LAWYER_ID = #{lawyerId}

SELECT COUNT(CONSULTING_CASE_REPLY_ID)
FROM TBL_CONSULTING_CASE_REPLY
WHERE LAWYER_ID = #{lawyerId}

mapper.update:
UPDATE TBL_LAWYER
SET LAWYER_PASSWORD = #{lawyerPassword}
WHERE LAWYER_ID = #{lawyerId}

UPDATE TBL_LAWYER
SET LAWYER_PHONE = #{lawyerPhone}
WHERE LAWYER_ID = #{lawyerId}
