mapper.select:
SELECT MEMBER_ID, MEMBER_EMAIL, MEMBER_PASSWORD, MEMBER_NAME, MEMBER_STATE, MEMBER_PHONE, MEMBER_LOGIN_CODE, CREATE_DATE, UPDATE_DATE
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail} AND MEMBER_PASSWORD = #{memberPassword}

SELECT LAWYER_ID, LAWYER_EMAIL, LAWYER_PASSWORD, LAWYER_NAME, LAWYER_STATE, LAWYER_PHONE, LAWYER_COMPANY, LAWYER_ADDRESS, LAWYER_QUALIFICATION, LAWYER_EDUCATION, LAWYER_IMAGE, CALL_PRICE, VIDEO_PRICE, VISIT_PRICE, CREATE_DATE, UPDATE_DATE
FROM TBL_LAWYER
WHERE LAWYER_EMAIL = #{lawyerEmail} AND LAWYER_PASSWORD = #{lawyerPassword}
